import React, { useState, useRef, useCallback, useEffect } from 'react';
import { CameraIcon, PhotoIcon, ArrowRightIcon, SparklesIcon, XMarkIcon, CheckIcon } from '@heroicons/react/24/outline';
import Webcam from 'react-webcam';
import { getHairstyles, createTryOnSession, uploadPhoto, getCurrentUser } from '../services/supabaseService';
import { handleImageError } from '../utils/imageUtils';
import type { Database } from '../lib/supabase';

type Hairstyle = Database['public']['Tables']['hairstyles']['Row'] & {
  hairstyle_categories: {
    id: string;
    name: string;
    description: string;
  };
};

export default function TryOnPage() {
  const [selectedImage, setSelectedImage] = useState<string | null>(null);
  const [selectedStyle, setSelectedStyle] = useState<Hairstyle | null>(null);
  const [hairstyles, setHairstyles] = useState<Hairstyle[]>([]);
  const [showCamera, setShowCamera] = useState(false);
  const [loading, setLoading] = useState(false);
  const [user, setUser] = useState<any>(null);
  const [activeCategory, setActiveCategory] = useState<'female' | 'male'>('female');
  const webcamRef = useRef<Webcam>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    loadHairstyles();
    getCurrentUser().then(setUser);
  }, []);

  const loadHairstyles = async () => {
    try {
      const data = await getHairstyles();
      setHairstyles(data as Hairstyle[]);
    } catch (error) {
      console.error('Error loading hairstyles:', error);
    }
  };

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result as string);
      };
      reader.readAsDataURL(file);
    }
  };

  const capture = useCallback(() => {
    const imageSrc = webcamRef.current?.getScreenshot();
    if (imageSrc) {
      setSelectedImage(imageSrc);
      setShowCamera(false);
    }
  }, [webcamRef]);

  const resetUpload = () => {
    setSelectedImage(null);
    setShowCamera(false);
    setSelectedStyle(null);
  };

  const handleSaveAndBook = async () => {
    if (!user) {
      alert('Please log in to save your try-on session');
      return;
    }

    if (!selectedImage || !selectedStyle) {
      alert('Please select both an image and a hairstyle');
      return;
    }

    try {
      setLoading(true);

      // Upload the original photo
      const file = dataURLtoFile(selectedImage, 'tryon-photo.jpg');
      const originalPhotoUrl = await uploadPhoto(file, 'tryon/originals');

      // Create try-on session
      await createTryOnSession({
        user_id: user.id,
        original_photo_url: originalPhotoUrl,
        hairstyle_id: selectedStyle.id,
        result_photo_url: originalPhotoUrl, // For now, using same image as result
        is_saved: true
      });

      alert('Try-on session saved successfully! You can now book an appointment.');
      // Navigate to booking page or show booking modal
    } catch (error) {
      console.error('Error saving try-on session:', error);
      alert('Failed to save try-on session. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Helper function to convert data URL to File
  const dataURLtoFile = (dataurl: string, filename: string): File => {
    const arr = dataurl.split(',');
    const mime = arr[0].match(/:(.*?);/)?.[1];
    const bstr = atob(arr[1]);
    let n = bstr.length;
    const u8arr = new Uint8Array(n);
    while (n--) {
      u8arr[n] = bstr.charCodeAt(n);
    }
    return new File([u8arr], filename, { type: mime });
  };

  return (
    <div className="h-screen bg-gray-900 flex">
      {/* Header */}
      <div className="absolute top-0 left-0 right-0 z-50 bg-gray-900 border-b border-gray-700 shadow-lg">
        <div className="flex items-center justify-between px-6 py-4">
          <div className="flex items-center space-x-6">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-gradient-to-r from-blue-500 to-purple-600 rounded-xl flex items-center justify-center shadow-lg">
                <SparklesIcon className="w-6 h-6 text-white" />
              </div>
              <div>
                <span className="text-white font-bold text-xl">Headz AI</span>
                <div className="text-xs text-gray-400">Professional Hair Studio</div>
              </div>
            </div>
            <div className="hidden md:flex items-center space-x-2 text-sm text-gray-300 bg-gray-800 px-3 py-1 rounded-full">
              <SparklesIcon className="w-4 h-4 text-blue-400" />
              <span>AI Hairstyle Editor</span>
            </div>
          </div>
          <div className="flex items-center space-x-4">
            <button className="bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 text-white px-6 py-2 rounded-lg text-sm font-medium transition-all duration-200 shadow-lg hover:shadow-xl">
              💾 Save Result
            </button>
            <div className="hidden md:block text-sm text-green-400 bg-green-900/20 px-3 py-1 rounded-full border border-green-700">
              ✨ Up to 30% Off
            </div>
          </div>
        </div>
      </div>

      {/* Sidebar */}
      <div className="w-80 bg-gray-900 border-r border-gray-700 pt-16 flex flex-col shadow-2xl">
        {/* Sidebar Header */}
        <div className="p-6 border-b border-gray-700 bg-gradient-to-r from-gray-800 to-gray-900">
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center space-x-3">
              <div className="w-8 h-8 bg-gradient-to-r from-purple-500 to-pink-500 rounded-lg flex items-center justify-center">
                <SparklesIcon className="w-5 h-5 text-white" />
              </div>
              <div>
                <span className="text-white font-semibold text-lg">AI Hairstyle</span>
                <div className="text-xs text-gray-400">Choose your perfect style</div>
              </div>
            </div>
            <button className="w-8 h-8 bg-gray-700 hover:bg-gray-600 rounded-lg flex items-center justify-center transition-colors">
              <XMarkIcon className="w-4 h-4 text-gray-300" />
            </button>
          </div>

          {/* Gender Selection */}
          <div className="space-y-3">
            <div className="text-xs text-gray-400 uppercase tracking-wider font-medium">Select Category</div>
            <div className="flex space-x-2 bg-gray-800 rounded-xl p-1 shadow-inner">
              <button
                onClick={() => setActiveCategory('female')}
                className={`flex-1 py-3 px-4 rounded-lg text-sm font-medium transition-all duration-200 ${
                  activeCategory === 'female'
                    ? 'bg-gradient-to-r from-pink-500 to-purple-600 text-white shadow-lg'
                    : 'text-gray-300 hover:text-white hover:bg-gray-700'
                }`}
              >
                <div className="flex items-center justify-center space-x-2">
                  <span className="text-lg">👩</span>
                  <span>Female</span>
                </div>
              </button>
              <button
                onClick={() => setActiveCategory('male')}
                className={`flex-1 py-3 px-4 rounded-lg text-sm font-medium transition-all duration-200 ${
                  activeCategory === 'male'
                    ? 'bg-gradient-to-r from-blue-500 to-cyan-600 text-white shadow-lg'
                    : 'text-gray-300 hover:text-white hover:bg-gray-700'
                }`}
              >
                <div className="flex items-center justify-center space-x-2">
                  <span className="text-lg">👨</span>
                  <span>Male</span>
                </div>
              </button>
            </div>
          </div>
        </div>

        {/* Hairstyle Grid */}
        <div className="flex-1 overflow-y-auto p-6">
          <div className="mb-4">
            <div className="text-xs text-gray-400 uppercase tracking-wider font-medium mb-3">Available Styles</div>
            <div className="text-sm text-gray-500">
              {hairstyles.filter(style => style.hairstyle_categories?.name.toLowerCase().includes(activeCategory)).length} styles found
            </div>
          </div>
          <div className="grid grid-cols-3 gap-3">
            {hairstyles
              .filter(style => style.hairstyle_categories?.name.toLowerCase().includes(activeCategory))
              .map((style) => (
              <div
                key={style.id}
                onClick={() => setSelectedStyle(style)}
                className={`group relative cursor-pointer rounded-xl overflow-hidden border-2 transition-all duration-200 hover:scale-105 ${
                  selectedStyle?.id === style.id
                    ? 'border-blue-500 ring-2 ring-blue-500/50 shadow-lg shadow-blue-500/25'
                    : 'border-gray-600 hover:border-gray-500 hover:shadow-lg'
                }`}
              >
                <img
                  src={style.image_url}
                  alt={style.name}
                  className="w-full h-24 object-cover transition-transform duration-200 group-hover:scale-110"
                  onError={(e) => handleImageError(e, 'hairstyle')}
                />
                <div className="absolute inset-x-0 bottom-0 bg-gradient-to-t from-black/80 to-transparent p-2">
                  <div className="text-xs text-white font-medium truncate">{style.name}</div>
                </div>
                {selectedStyle?.id === style.id && (
                  <div className="absolute inset-0 bg-blue-500/30 flex items-center justify-center">
                    <div className="w-8 h-8 bg-blue-500 rounded-full flex items-center justify-center shadow-lg">
                      <CheckIcon className="w-5 h-5 text-white" />
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
          {hairstyles.filter(style => style.hairstyle_categories?.name.toLowerCase().includes(activeCategory)).length === 0 && (
            <div className="text-center py-12">
              <div className="text-gray-500 mb-2">No styles found</div>
              <div className="text-sm text-gray-600">Try switching categories</div>
            </div>
          )}
        </div>

        {/* Sidebar Footer */}
        <div className="p-6 border-t border-gray-700 bg-gray-800/50">
          {selectedStyle && (
            <div className="mb-4 p-3 bg-gray-800 rounded-lg border border-gray-600">
              <div className="text-sm text-white font-medium">{selectedStyle.name}</div>
              <div className="text-xs text-gray-400 capitalize">{selectedStyle.length} • {selectedStyle.hairstyle_categories?.name}</div>
            </div>
          )}
          <div className="flex space-x-3">
            <button
              onClick={() => setSelectedStyle(null)}
              className="flex-1 bg-gray-700 hover:bg-gray-600 text-white py-3 px-4 rounded-lg text-sm font-medium transition-all duration-200 hover:shadow-lg"
            >
              🔄 Reset
            </button>
            <button
              onClick={handleSaveAndBook}
              disabled={!selectedStyle || loading}
              className="flex-1 bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 disabled:from-gray-600 disabled:to-gray-600 disabled:cursor-not-allowed text-white py-3 px-4 rounded-lg text-sm font-medium transition-all duration-200 hover:shadow-lg disabled:shadow-none"
            >
              {loading ? '⏳ Applying...' : '✨ Apply Style'}
            </button>
          </div>
        </div>
      </div>

      {/* Main Canvas Area */}
      <div className="flex-1 pt-16 bg-gray-100 flex flex-col">
        {/* Canvas Content */}
        <div className="flex-1 flex items-center justify-center p-8">
          {!selectedImage && !showCamera ? (
            /* Upload Area */
            <div className="bg-white rounded-lg border-2 border-dashed border-gray-300 p-12 text-center max-w-md w-full">
              <div className="mb-4">
                <PhotoIcon className="mx-auto h-12 w-12 text-gray-400" />
              </div>
              <h3 className="text-lg font-medium text-gray-900 mb-2">
                Drag or upload your own images
              </h3>
              <p className="text-sm text-gray-500 mb-6">
                Click to view the upload guide for best results
              </p>
              <div className="space-y-3">
                <button
                  onClick={() => fileInputRef.current?.click()}
                  className="w-full bg-blue-600 hover:bg-blue-700 text-white py-3 px-6 rounded-lg font-medium transition-colors"
                >
                  📁 Upload Image
                </button>
                <button
                  onClick={() => setShowCamera(true)}
                  className="w-full bg-gray-600 hover:bg-gray-700 text-white py-3 px-6 rounded-lg font-medium transition-colors"
                >
                  📷 Use Camera
                </button>
              </div>
              <input
                ref={fileInputRef}
                type="file"
                className="hidden"
                accept="image/*"
                onChange={handleFileUpload}
              />
              <div className="mt-6 text-xs text-gray-400">
                No photos? Try one of ours.
              </div>
              <div className="flex justify-center space-x-2 mt-2">
                <div className="w-8 h-8 bg-gray-200 rounded-full"></div>
                <div className="w-8 h-8 bg-gray-200 rounded-full"></div>
                <div className="w-8 h-8 bg-gray-200 rounded-full"></div>
              </div>
            </div>
          ) : showCamera ? (
            /* Camera View */
            <div className="bg-white rounded-lg p-6 max-w-2xl w-full">
              <div className="text-center mb-4">
                <h3 className="text-lg font-medium text-gray-900">Take Your Photo</h3>
                <p className="text-sm text-gray-500">Position yourself in the frame</p>
              </div>
              <div className="relative">
                <Webcam
                  ref={webcamRef}
                  screenshotFormat="image/jpeg"
                  className="w-full rounded-lg"
                />
              </div>
              <div className="mt-4 flex justify-center space-x-4">
                <button
                  onClick={capture}
                  className="bg-blue-600 hover:bg-blue-700 text-white py-2 px-6 rounded-lg font-medium transition-colors"
                >
                  📷 Capture
                </button>
                <button
                  onClick={() => setShowCamera(false)}
                  className="bg-gray-600 hover:bg-gray-700 text-white py-2 px-6 rounded-lg font-medium transition-colors"
                >
                  Cancel
                </button>
              </div>
            </div>
          ) : (
            /* Image Preview with Hairstyle */
            <div className="bg-white rounded-lg p-6 max-w-4xl w-full">
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                {/* Original Image */}
                <div>
                  <h3 className="text-lg font-medium text-gray-900 mb-4 text-center">Original</h3>
                  <div className="relative">
                    {selectedImage && (
                      <img
                        src={selectedImage}
                        alt="Original"
                        className="w-full rounded-lg shadow-md"
                      />
                    )}
                  </div>
                </div>

                {/* Preview with Hairstyle */}
                <div>
                  <h3 className="text-lg font-medium text-gray-900 mb-4 text-center">
                    {selectedStyle ? `With ${selectedStyle.name}` : 'Select a hairstyle'}
                  </h3>
                  <div className="relative">
                    {selectedImage && (
                      <img
                        src={selectedImage}
                        alt="With hairstyle"
                        className="w-full rounded-lg shadow-md"
                      />
                    )}
                    {selectedStyle && (
                      <>
                        <div className="absolute inset-0 bg-gradient-to-b from-blue-500/20 to-transparent rounded-lg pointer-events-none" />
                        <div className="absolute top-4 right-4 bg-white/90 backdrop-blur-sm px-3 py-1 rounded-full">
                          <span className="text-sm font-medium text-gray-700">{selectedStyle.name}</span>
                        </div>
                      </>
                    )}
                  </div>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="mt-8 flex justify-center space-x-4">
                <button
                  onClick={resetUpload}
                  className="bg-gray-600 hover:bg-gray-700 text-white py-2 px-6 rounded-lg font-medium transition-colors"
                >
                  Use Different Photo
                </button>
                {selectedStyle && (
                  <button
                    onClick={handleSaveAndBook}
                    disabled={loading}
                    className="bg-blue-600 hover:bg-blue-700 disabled:bg-blue-400 text-white py-2 px-6 rounded-lg font-medium transition-colors"
                  >
                    {loading ? 'Saving...' : 'Save & Book Appointment'}
                  </button>
                )}
              </div>
            </div>
          )}
        </div>
      </div>

    </div>
  );
}
