import React, { useState, useRef, useCallback, useEffect } from 'react';
import { CameraIcon, PhotoIcon, ArrowRightIcon, SparklesIcon, XMarkIcon, CheckIcon, ChevronDownIcon, PlusIcon } from '@heroicons/react/24/outline';
import Webcam from 'react-webcam';
import { getHairstyles, createTryOnSession, uploadPhoto, getCurrentUser } from '../services/supabaseService';
import { handleImageError } from '../utils/imageUtils';
import type { Database } from '../lib/supabase';

type Hairstyle = Database['public']['Tables']['hairstyles']['Row'] & {
  hairstyle_categories: {
    id: string;
    name: string;
    description: string;
  };
};

export default function TryOnPage() {
  const [selectedImage, setSelectedImage] = useState<string | null>(null);
  const [selectedStyle, setSelectedStyle] = useState<Hairstyle | null>(null);
  const [hairstyles, setHairstyles] = useState<Hairstyle[]>([]);
  const [showCamera, setShowCamera] = useState(false);
  const [loading, setLoading] = useState(false);
  const [user, setUser] = useState<any>(null);
  const [activeCategory, setActiveCategory] = useState<'female' | 'male'>('female');
  const webcamRef = useRef<Webcam>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    loadHairstyles();
    getCurrentUser().then(setUser);
  }, []);

  const loadHairstyles = async () => {
    try {
      const data = await getHairstyles();
      setHairstyles(data as Hairstyle[]);
    } catch (error) {
      console.error('Error loading hairstyles:', error);
    }
  };

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result as string);
      };
      reader.readAsDataURL(file);
    }
  };

  const capture = useCallback(() => {
    const imageSrc = webcamRef.current?.getScreenshot();
    if (imageSrc) {
      setSelectedImage(imageSrc);
      setShowCamera(false);
    }
  }, [webcamRef]);

  const resetUpload = () => {
    setSelectedImage(null);
    setShowCamera(false);
    setSelectedStyle(null);
  };

  const handleSaveAndBook = async () => {
    if (!user) {
      alert('Please log in to save your try-on session');
      return;
    }

    if (!selectedImage || !selectedStyle) {
      alert('Please select both an image and a hairstyle');
      return;
    }

    try {
      setLoading(true);

      // Upload the original photo
      const file = dataURLtoFile(selectedImage, 'tryon-photo.jpg');
      const originalPhotoUrl = await uploadPhoto(file, 'tryon/originals');

      // Create try-on session
      await createTryOnSession({
        user_id: user.id,
        original_photo_url: originalPhotoUrl,
        hairstyle_id: selectedStyle.id,
        result_photo_url: originalPhotoUrl, // For now, using same image as result
        is_saved: true
      });

      alert('Try-on session saved successfully! You can now book an appointment.');
      // Navigate to booking page or show booking modal
    } catch (error) {
      console.error('Error saving try-on session:', error);
      alert('Failed to save try-on session. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Helper function to convert data URL to File
  const dataURLtoFile = (dataurl: string, filename: string): File => {
    const arr = dataurl.split(',');
    const mime = arr[0].match(/:(.*?);/)?.[1];
    const bstr = atob(arr[1]);
    let n = bstr.length;
    const u8arr = new Uint8Array(n);
    while (n--) {
      u8arr[n] = bstr.charCodeAt(n);
    }
    return new File([u8arr], filename, { type: mime });
  };

  return (
    <div className="h-screen bg-gray-800 flex">
      {/* Header */}
      <div className="absolute top-0 left-0 right-0 z-50 bg-gray-800 border-b border-gray-700">
        <div className="flex items-center justify-between px-4 py-3">
          <div className="flex items-center space-x-4">
            <div className="flex items-center space-x-2">
              <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-purple-600 rounded-lg flex items-center justify-center">
                <SparklesIcon className="w-5 h-5 text-white" />
              </div>
              <span className="text-white font-semibold text-lg">fotor</span>
            </div>
            <div className="flex items-center space-x-2 text-sm text-gray-300">
              <span>AI Photo Editor</span>
              <ChevronDownIcon className="w-4 h-4" />
            </div>
            <button className="text-gray-300 hover:text-white">
              <PlusIcon className="w-5 h-5" />
              <span className="ml-1 text-sm">Upload Image</span>
            </button>
          </div>
          <div className="flex items-center space-x-4">
            <button className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition-colors">
              Download
            </button>
            <div className="text-sm text-yellow-400">🏷️ Up to 30% Off</div>
            <div className="w-8 h-8 bg-gray-600 rounded-full"></div>
          </div>
        </div>
      </div>

      {/* Left Sidebar - Fotor Style */}
      <div className="w-64 bg-gray-900 pt-16 flex flex-col border-r border-gray-700">
        {/* Sidebar Tools */}
        <div className="flex-1 p-4">
          {/* AI Tools Section */}
          <div className="mb-6">
            <div className="flex items-center space-x-3 mb-4 p-2 hover:bg-gray-800 rounded-lg cursor-pointer">
              <SparklesIcon className="w-5 h-5 text-blue-400" />
              <span className="text-white text-sm font-medium">AI Tools</span>
            </div>
          </div>

          {/* Adjust Section */}
          <div className="mb-6">
            <div className="flex items-center space-x-3 mb-4 p-2 hover:bg-gray-800 rounded-lg cursor-pointer">
              <div className="w-5 h-5 bg-gray-600 rounded flex items-center justify-center">
                <div className="w-3 h-3 bg-gray-400 rounded"></div>
              </div>
              <span className="text-white text-sm font-medium">Adjust</span>
            </div>
          </div>

          {/* Effects Section */}
          <div className="mb-6">
            <div className="flex items-center space-x-3 mb-4 p-2 hover:bg-gray-800 rounded-lg cursor-pointer">
              <div className="w-5 h-5 bg-gray-600 rounded flex items-center justify-center">
                <div className="w-3 h-3 bg-gray-400 rounded"></div>
              </div>
              <span className="text-white text-sm font-medium">Effects</span>
            </div>
          </div>

          {/* Beauty Section */}
          <div className="mb-6">
            <div className="flex items-center space-x-3 mb-4 p-2 hover:bg-gray-800 rounded-lg cursor-pointer">
              <div className="w-5 h-5 bg-gray-600 rounded flex items-center justify-center">
                <div className="w-3 h-3 bg-gray-400 rounded"></div>
              </div>
              <span className="text-white text-sm font-medium">Beauty</span>
            </div>
          </div>

          {/* Frames Section */}
          <div className="mb-6">
            <div className="flex items-center space-x-3 mb-4 p-2 hover:bg-gray-800 rounded-lg cursor-pointer">
              <div className="w-5 h-5 bg-gray-600 rounded flex items-center justify-center">
                <div className="w-3 h-3 bg-gray-400 rounded"></div>
              </div>
              <span className="text-white text-sm font-medium">Frames</span>
            </div>
          </div>

          {/* Text Section */}
          <div className="mb-6">
            <div className="flex items-center space-x-3 mb-4 p-2 hover:bg-gray-800 rounded-lg cursor-pointer">
              <div className="w-5 h-5 bg-gray-600 rounded flex items-center justify-center">
                <div className="w-3 h-3 bg-gray-400 rounded"></div>
              </div>
              <span className="text-white text-sm font-medium">Text</span>
            </div>
          </div>

          {/* Overlays Section */}
          <div className="mb-6">
            <div className="flex items-center space-x-3 mb-4 p-2 hover:bg-gray-800 rounded-lg cursor-pointer">
              <div className="w-5 h-5 bg-gray-600 rounded flex items-center justify-center">
                <div className="w-3 h-3 bg-gray-400 rounded"></div>
              </div>
              <span className="text-white text-sm font-medium">Overlays</span>
            </div>
          </div>

          {/* More Section */}
          <div className="mb-6">
            <div className="flex items-center space-x-3 mb-4 p-2 hover:bg-gray-800 rounded-lg cursor-pointer">
              <div className="w-5 h-5 bg-gray-600 rounded flex items-center justify-center">
                <div className="w-3 h-3 bg-gray-400 rounded"></div>
              </div>
              <span className="text-white text-sm font-medium">More</span>
            </div>
          </div>
        </div>
      </div>

      {/* AI Hairstyle Panel */}
      <div className="w-80 bg-gray-800 border-r border-gray-600 pt-16 flex flex-col">
        {/* Panel Header */}
        <div className="p-4 border-b border-gray-600">
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center space-x-2">
              <SparklesIcon className="w-5 h-5 text-blue-400" />
              <span className="text-white font-medium">AI Hairstyle</span>
            </div>
            <button className="text-gray-400 hover:text-white">
              <XMarkIcon className="w-5 h-5" />
            </button>
          </div>

          {/* Gender Selection */}
          <div className="space-y-3">
            <div className="flex space-x-2">
              <button
                onClick={() => setActiveCategory('female')}
                className={`flex-1 py-2 px-3 rounded-lg text-sm font-medium transition-all duration-200 ${
                  activeCategory === 'female'
                    ? 'bg-blue-600 text-white'
                    : 'bg-gray-700 text-gray-300 hover:bg-gray-600'
                }`}
              >
                👩 Female
              </button>
              <button
                onClick={() => setActiveCategory('male')}
                className={`flex-1 py-2 px-3 rounded-lg text-sm font-medium transition-all duration-200 ${
                  activeCategory === 'male'
                    ? 'bg-blue-600 text-white'
                    : 'bg-gray-700 text-gray-300 hover:bg-gray-600'
                }`}
              >
                👨 Male
              </button>
            </div>
          </div>
        </div>

        {/* Hairstyle Grid */}
        <div className="flex-1 overflow-y-auto p-4">
          {/* Sample Hairstyles Grid - 3x3 layout like Fotor */}
          <div className="grid grid-cols-3 gap-2 mb-4">
            {/* Row 1 */}
            <div className="aspect-square bg-gray-700 rounded-lg overflow-hidden cursor-pointer hover:ring-2 hover:ring-blue-400 transition-all">
              <img src="/api/placeholder/80/80" alt="Hairstyle 1" className="w-full h-full object-cover" />
            </div>
            <div className="aspect-square bg-gray-700 rounded-lg overflow-hidden cursor-pointer hover:ring-2 hover:ring-blue-400 transition-all">
              <img src="/api/placeholder/80/80" alt="Hairstyle 2" className="w-full h-full object-cover" />
            </div>
            <div className="aspect-square bg-gray-700 rounded-lg overflow-hidden cursor-pointer hover:ring-2 hover:ring-blue-400 transition-all">
              <img src="/api/placeholder/80/80" alt="Hairstyle 3" className="w-full h-full object-cover" />
            </div>

            {/* Row 2 */}
            <div className="aspect-square bg-gray-700 rounded-lg overflow-hidden cursor-pointer hover:ring-2 hover:ring-blue-400 transition-all">
              <img src="/api/placeholder/80/80" alt="Hairstyle 4" className="w-full h-full object-cover" />
            </div>
            <div className="aspect-square bg-gray-700 rounded-lg overflow-hidden cursor-pointer hover:ring-2 hover:ring-blue-400 transition-all">
              <img src="/api/placeholder/80/80" alt="Hairstyle 5" className="w-full h-full object-cover" />
            </div>
            <div className="aspect-square bg-gray-700 rounded-lg overflow-hidden cursor-pointer hover:ring-2 hover:ring-blue-400 transition-all">
              <img src="/api/placeholder/80/80" alt="Hairstyle 6" className="w-full h-full object-cover" />
            </div>

            {/* Row 3 */}
            <div className="aspect-square bg-gray-700 rounded-lg overflow-hidden cursor-pointer hover:ring-2 hover:ring-blue-400 transition-all">
              <img src="/api/placeholder/80/80" alt="Hairstyle 7" className="w-full h-full object-cover" />
            </div>
            <div className="aspect-square bg-gray-700 rounded-lg overflow-hidden cursor-pointer hover:ring-2 hover:ring-blue-400 transition-all">
              <img src="/api/placeholder/80/80" alt="Hairstyle 8" className="w-full h-full object-cover" />
            </div>
            <div className="aspect-square bg-gray-700 rounded-lg overflow-hidden cursor-pointer hover:ring-2 hover:ring-blue-400 transition-all">
              <img src="/api/placeholder/80/80" alt="Hairstyle 9" className="w-full h-full object-cover" />
            </div>
          </div>

          {/* Color Palette */}
          <div className="flex space-x-2 mb-4">
            <div className="w-6 h-6 bg-gray-300 rounded-full cursor-pointer hover:ring-2 hover:ring-white"></div>
            <div className="w-6 h-6 bg-gray-800 rounded-full cursor-pointer hover:ring-2 hover:ring-white"></div>
            <div className="w-6 h-6 bg-yellow-600 rounded-full cursor-pointer hover:ring-2 hover:ring-white"></div>
            <div className="w-6 h-6 bg-yellow-800 rounded-full cursor-pointer hover:ring-2 hover:ring-white"></div>
            <div className="w-6 h-6 bg-red-800 rounded-full cursor-pointer hover:ring-2 hover:ring-white"></div>
            <div className="w-6 h-6 bg-black rounded-full cursor-pointer hover:ring-2 hover:ring-white"></div>
            <div className="w-6 h-6 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full cursor-pointer hover:ring-2 hover:ring-white"></div>
          </div>

          {/* Generate Button */}
          <div className="space-y-3">
            <button className="w-full bg-gradient-to-r from-cyan-500 to-blue-600 text-white py-3 px-4 rounded-lg font-medium hover:from-cyan-600 hover:to-blue-700 transition-all">
              ⚡ Generate ✨ 2
            </button>
            <button className="w-full bg-blue-600 text-white py-2 px-4 rounded-lg font-medium hover:bg-blue-700 transition-all">
              Apply
            </button>
            <button className="w-full bg-gray-600 text-white py-2 px-4 rounded-lg font-medium hover:bg-gray-700 transition-all">
              Cancel
            </button>
          </div>
        </div>

      </div>

      {/* Main Canvas Area - Fotor Style */}
      <div className="flex-1 pt-16 bg-gray-700 flex flex-col">
        {/* Canvas Content */}
        <div className="flex-1 flex items-center justify-center p-8">
          {!selectedImage && !showCamera ? (
            /* Upload Area - Fotor Style */
            <div className="text-center max-w-lg w-full">
              {/* Upload Icon */}
              <div className="mb-6">
                <div className="w-16 h-16 bg-blue-600 rounded-full flex items-center justify-center mx-auto mb-4 relative">
                  <PhotoIcon className="h-8 w-8 text-white" />
                  <div className="absolute -top-1 -right-1 w-6 h-6 bg-blue-500 rounded-full flex items-center justify-center">
                    <PlusIcon className="w-4 h-4 text-white" />
                  </div>
                </div>
              </div>

              <h3 className="text-xl font-medium text-white mb-2">
                Drag or upload your own images
              </h3>
              <p className="text-gray-300 mb-6">
                Click to <span className="text-blue-400 underline cursor-pointer">view the upload guide</span> for best results
              </p>

              {/* Upload Button */}
              <div className="mb-8">
                <button
                  onClick={() => fileInputRef.current?.click()}
                  className="bg-gradient-to-r from-cyan-500 to-blue-600 hover:from-cyan-600 hover:to-blue-700 text-white py-3 px-8 rounded-full font-medium transition-all duration-200 shadow-lg hover:shadow-xl"
                >
                  <div className="flex items-center justify-center space-x-2">
                    <PlusIcon className="w-5 h-5" />
                    <span>Upload Image</span>
                  </div>
                </button>
              </div>

              {/* Sample Images */}
              <div className="text-gray-400 text-sm mb-4">No photos? Try one of ours.</div>
              <div className="flex justify-center space-x-3">
                <div className="w-12 h-12 bg-gray-600 rounded-lg overflow-hidden cursor-pointer hover:ring-2 hover:ring-blue-400 transition-all">
                  <img src="/api/placeholder/48/48" alt="Sample 1" className="w-full h-full object-cover" />
                </div>
                <div className="w-12 h-12 bg-gray-600 rounded-lg overflow-hidden cursor-pointer hover:ring-2 hover:ring-blue-400 transition-all">
                  <img src="/api/placeholder/48/48" alt="Sample 2" className="w-full h-full object-cover" />
                </div>
                <div className="w-12 h-12 bg-gray-600 rounded-lg overflow-hidden cursor-pointer hover:ring-2 hover:ring-blue-400 transition-all">
                  <img src="/api/placeholder/48/48" alt="Sample 3" className="w-full h-full object-cover" />
                </div>
              </div>
              <input
                ref={fileInputRef}
                type="file"
                className="hidden"
                accept="image/*"
                onChange={handleFileUpload}
              />
              <div className="mt-8 pt-6 border-t border-gray-200">
                <div className="text-sm text-gray-500 mb-3">
                  No photos? Try one of ours.
                </div>
                <div className="flex justify-center space-x-3">
                  <div className="w-12 h-12 bg-gradient-to-r from-pink-400 to-red-400 rounded-xl shadow-md hover:shadow-lg transition-shadow cursor-pointer"></div>
                  <div className="w-12 h-12 bg-gradient-to-r from-blue-400 to-cyan-400 rounded-xl shadow-md hover:shadow-lg transition-shadow cursor-pointer"></div>
                  <div className="w-12 h-12 bg-gradient-to-r from-green-400 to-emerald-400 rounded-xl shadow-md hover:shadow-lg transition-shadow cursor-pointer"></div>
                </div>
              </div>
            </div>
          ) : showCamera ? (
            /* Camera View */
            <div className="bg-white rounded-2xl p-8 max-w-2xl w-full shadow-2xl">
              <div className="text-center mb-6">
                <div className="w-16 h-16 bg-gradient-to-r from-green-500 to-emerald-600 rounded-full flex items-center justify-center mx-auto mb-4 shadow-lg">
                  <CameraIcon className="h-8 w-8 text-white" />
                </div>
                <h3 className="text-2xl font-bold text-gray-900 mb-2">Take Your Photo</h3>
                <p className="text-gray-600">Position yourself in the frame and smile!</p>
              </div>
              <div className="relative rounded-xl overflow-hidden shadow-lg">
                <Webcam
                  ref={webcamRef}
                  screenshotFormat="image/jpeg"
                  className="w-full rounded-xl"
                />
                <div className="absolute inset-0 border-4 border-white/20 rounded-xl pointer-events-none"></div>
              </div>
              <div className="mt-6 flex justify-center space-x-4">
                <button
                  onClick={capture}
                  className="bg-gradient-to-r from-green-600 to-emerald-700 hover:from-green-700 hover:to-emerald-800 text-white py-3 px-8 rounded-xl font-semibold transition-all duration-200 shadow-lg hover:shadow-xl transform hover:-translate-y-1"
                >
                  <div className="flex items-center space-x-2">
                    <span className="text-xl">📷</span>
                    <span>Capture Photo</span>
                  </div>
                </button>
                <button
                  onClick={() => setShowCamera(false)}
                  className="bg-gradient-to-r from-gray-600 to-gray-700 hover:from-gray-700 hover:to-gray-800 text-white py-3 px-8 rounded-xl font-semibold transition-all duration-200 shadow-lg hover:shadow-xl transform hover:-translate-y-1"
                >
                  Cancel
                </button>
              </div>
            </div>
          ) : (
            /* Image Preview with Hairstyle */
            <div className="bg-white rounded-2xl p-8 max-w-6xl w-full shadow-2xl">
              <div className="text-center mb-8">
                <h2 className="text-3xl font-bold text-gray-900 mb-2">Your Virtual Makeover</h2>
                <p className="text-gray-600">Compare your original photo with the AI-generated hairstyle</p>
              </div>

              <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                {/* Original Image */}
                <div className="space-y-4">
                  <div className="flex items-center justify-center space-x-2 mb-4">
                    <div className="w-3 h-3 bg-gray-400 rounded-full"></div>
                    <h3 className="text-xl font-semibold text-gray-900">Original</h3>
                  </div>
                  <div className="relative group">
                    {selectedImage && (
                      <img
                        src={selectedImage}
                        alt="Original"
                        className="w-full rounded-xl shadow-lg group-hover:shadow-2xl transition-shadow duration-300"
                      />
                    )}
                    <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent rounded-xl opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                  </div>
                </div>

                {/* Preview with Hairstyle */}
                <div className="space-y-4">
                  <div className="flex items-center justify-center space-x-2 mb-4">
                    <div className={`w-3 h-3 rounded-full ${selectedStyle ? 'bg-green-500' : 'bg-gray-300'}`}></div>
                    <h3 className="text-xl font-semibold text-gray-900">
                      {selectedStyle ? `With ${selectedStyle.name}` : 'Select a hairstyle from sidebar'}
                    </h3>
                  </div>
                  <div className="relative group">
                    {selectedImage ? (
                      <div className="relative">
                        <img
                          src={selectedImage}
                          alt="With hairstyle"
                          className="w-full rounded-xl shadow-lg group-hover:shadow-2xl transition-shadow duration-300"
                        />
                        {selectedStyle ? (
                          <>
                            <div className="absolute inset-0 bg-gradient-to-b from-blue-500/20 via-purple-500/10 to-transparent rounded-xl pointer-events-none" />
                            <div className="absolute top-4 right-4 bg-gradient-to-r from-blue-600 to-purple-600 text-white px-4 py-2 rounded-full shadow-lg">
                              <div className="flex items-center space-x-2">
                                <SparklesIcon className="w-4 h-4" />
                                <span className="text-sm font-medium">{selectedStyle.name}</span>
                              </div>
                            </div>
                            <div className="absolute bottom-4 left-4 bg-white/90 backdrop-blur-sm px-3 py-1 rounded-full">
                              <span className="text-sm font-medium text-gray-700">✨ AI Enhanced</span>
                            </div>
                          </>
                        ) : (
                          <div className="absolute inset-0 bg-gray-900/50 rounded-xl flex items-center justify-center">
                            <div className="text-center text-white">
                              <SparklesIcon className="w-12 h-12 mx-auto mb-2 opacity-50" />
                              <p className="text-lg font-medium">Choose a style</p>
                              <p className="text-sm opacity-75">Select from sidebar to preview</p>
                            </div>
                          </div>
                        )}
                      </div>
                    ) : (
                      <div className="w-full h-64 bg-gray-100 rounded-xl flex items-center justify-center">
                        <div className="text-center text-gray-500">
                          <PhotoIcon className="w-12 h-12 mx-auto mb-2" />
                          <p>Upload a photo first</p>
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="mt-10 flex flex-wrap justify-center gap-4">
                <button
                  onClick={resetUpload}
                  className="bg-gradient-to-r from-gray-600 to-gray-700 hover:from-gray-700 hover:to-gray-800 text-white py-3 px-8 rounded-xl font-semibold transition-all duration-200 shadow-lg hover:shadow-xl transform hover:-translate-y-1"
                >
                  <div className="flex items-center space-x-2">
                    <span>🔄</span>
                    <span>Use Different Photo</span>
                  </div>
                </button>
                {selectedStyle && (
                  <button
                    onClick={handleSaveAndBook}
                    disabled={loading}
                    className="bg-gradient-to-r from-green-600 to-emerald-700 hover:from-green-700 hover:to-emerald-800 disabled:from-gray-400 disabled:to-gray-500 text-white py-3 px-8 rounded-xl font-semibold transition-all duration-200 shadow-lg hover:shadow-xl transform hover:-translate-y-1 disabled:transform-none"
                  >
                    <div className="flex items-center space-x-2">
                      <span>{loading ? '⏳' : '💾'}</span>
                      <span>{loading ? 'Saving...' : 'Save & Book Appointment'}</span>
                    </div>
                  </button>
                )}
              </div>
            </div>
          )}
        </div>
      </div>

    </div>
  );
}
